import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  const [todos, setTodos] = useState([
    { id: 1, content: "Send E-mail", isDone: false },
    { id: 2, content: "Make Work-Books", isDone: false },
    { id: 3, content: "Sleeping", isDone: true },
    { id: 4, content: "Watching You-Tube", isDone: true },
  ]);

  const toggleTodo = (id) => {
    setTodos((prevTodos) =>
      prevTodos.map((todo) =>
        todo.id === id ? { ...todo, isDone: !todo.isDone } : todo
      )
    );
  };

  return (
    <div className="App">
      <div className="container">
        <h1 id="title">UMC Study Plan</h1>
        <div className="number">
          <input
            type="text"
            id="input-box"
            placeholder="UMC 스터디 계획을 작성해보세요!"
          ></input>
        </div>
        <div id="todos-container">
          <div id="todo-list">
            <h2>해야 할 일</h2>
            <ul>
              {todos
                .filter((todo) => !todo.isDone)
                .map((todo) => (
                  <li
                    key={todo.id}
                    onClick={() => toggleTodo(todo.id)}
                    style={{ cursor: "pointer" }}
                  >
                    {todo.content}
                  </li>
                ))}
            </ul>
          </div>
          <div id="completed-list">
            <h2>해낸 일</h2>
            <ul>
              {todos
                .filter((todo) => todo.isDone)
                .map((todo) => (
                  <li
                    key={todo.id}
                    onClick={() => toggleTodo(todo.id)}
                    style={{ cursor: "pointer", textDecoration: "line-through" }}
                  >
                    {todo.content}
                  </li>
                ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
